/**
 * @brief getPlane
 * @param a
 * @param b
 * @param c
 * @return
 * The plane on which a,b,c lie
 */
template<class T>
Vector4<T> getPlane(const Vector3<T>& a,
                    const Vector3<T>& b,
                    const Vector3<T>& c){

    Vector3<T> n=(b-a).cross(c-a);
    n.normalize();

    T d=n.dot(a);
    Vector4<T> N(n[0],n[1],n[2],-d);
    return N;
}
template<class T> inline T signedDistanceFromPlane(const cvl::Vector4<T>& plane,const cvl::Vector3<T>& x){
 T v=plane[0]*x[0] + plane[1]*x[1] + plane[2]*x[2] + plane[3];
 // line normed planes dont need this...
 T ln=T(1.0)/sqrt(plane[0]*plane[0]+plane[1]*plane[1]+plane[2]*plane[2]);
 v*=ln;
 return v;
}



/**
 * @brief getRoots
 * @param p the polynomial p[0]*x^2 + p[1]*x + p[2] = 0
 * @return the roots
 * Note that the type has to support complex numbers if the roots can be complex!
 * Preferably use std::complex<double> or if that is unavail use cvl::Complex
 */
template<class T>
Vector2<T> getRoots(const Vector3<T>& p){
    T sq=sqrt(p[1]*p[1] -4*p[0]*p[2]);
    return Vector2<T>((-p[1]+sq)/(2*p[0]),(-p[1]-sq)/(2*p[0]));
}
/**
 * @brief getRoots
 * @param p the polynomial p[0]*x^3 + p[1]*x^2 + p[2]*x + p[3] = 0
 * @return the roots
 * Note that the type has to support complex numbers if the roots can be complex!
 * Preferably use std::complex<double> or if that is unavail use cvl::Complex
 */
template<class T>
Vector3<T> getRoots(const Vector4<T>& p){
    T pa=p[1]/p[0];
    T pb=p[2]/p[0];
    T pc=p[3]/p[0];
    T v0  = (- pa*pa/9.0 + pb/3.0);
    T v00 = (pa*pa*pa/27 - (pb*pa)/6.0 + pc/2.0);
    T v2  = - pa*pa/9.0 + pb/3.0;


    T v1 = (pa*pb)/6.0 - pc/2.0 + sqrt(v0*v0*v0 + v00*v00) - pa*pa*pa/27.0;
    T v4 = pow(v1,1.0/3.0);
    T v3 = 1.0/v4;
    T v5(0.0,std::sqrt(3.0));
    T v6=(v2*v3) - v4 - pa*(2.0/3.0);
    T v7=(v4 + v2*v3)*v5;

    T r1=v4 - pa/3.0 - v2*v3;
    T r2=(v6 - v7)/2.0;
    T r3=(v6 + v7)/2.0;

    return Vector3<T>(r1,r2,r3);
}

template<class T>
/**
 * @brief normalizeplane
 * @param plane
 * normalizes the plane n,d so |n|=1 and so d=-1;
 * actually its a special case of "line"norm but with a different standard for the sign...
 * Though math is consistent mathematicians really arent...
 */
void normalizeplane(Vector4<T>& plane){
    plane/=Vector3<T>(plane(0),plane(1),plane(2)).length();
    if(plane(3)>0) plane=-plane;
}

template<class T>

/**
 * @brief getHomography
 * @param Pab
 * @param plane in b
 * @return the homography which transforms from b to a
 *
 * so if I(y)=1
 * I2(H*y)=1 and I2_cam=P*I1_cam
 * I think... untested...
 * \todo verify assumption, improve text...
 */

Matrix<T,3,3> getHomography(const Pose<T>& Pab, Vector4<T> plane_b){
    // plane norm needed? no not really, the plane.dehom kills it
    normalizeplane(plane_b);    
    Matrix<T,3,3> H=Pab.getR() - Pab.getT()*(plane_b.dehom()).transpose();
return H;
}



